import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import '../App.css';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Analysis = () => {
  const [files, setFiles] = useState([]);
  const [guideSeq, setGuideSeq] = useState('TTAGATTAGATTAGATTAGATTAGATTAGATTAGAC');
  const [metadata, setMetadata] = useState([]);
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [processingStatus, setProcessingStatus] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files);
    const validFiles = uploadedFiles.filter(file => file.name.endsWith('.ab1') && file.size <= 10 * 1024 * 1024);

    if (validFiles.length !== uploadedFiles.length) {
      setError('Some files are invalid. Please upload .ab1 files under 10MB.');
      return;
    }

    setFiles(validFiles);
    setMetadata(validFiles.map(file => ({ fileName: file.name, group: '', replicate: '1' })));
    setError(null);
    setResults(null);
  };

  const handleMetadataChange = (index, field, value) => {
    const newMetadata = [...metadata];
    newMetadata[index][field] = value;
    setMetadata(newMetadata);
  };

  const validateInput = () => {
    if (!/^[ATCG]+$/.test(guideSeq)) {
      setError('Guide sequence must contain only A, T, C, G characters.');
      return false;
    }

    const missingGroups = metadata.some(m => !m.group.trim());
    if (missingGroups) {
      setError('Each file must have a group assigned.');
      return false;
    }

    return true;
  };

  const processFiles = async () => {
    if (!validateInput()) return;

    setLoading(true);
    setError(null);
    setProcessingStatus('Uploading and analyzing files...');

    try {
      const formData = new FormData();
      files.forEach(file => formData.append('files', file));
      formData.append('guide_seq', guideSeq);
      formData.append('groups', JSON.stringify(metadata.map(m => m.group)));
      formData.append('replicates', JSON.stringify(metadata.map(m => m.replicate)));

      const response = await fetch(`${API_URL}/analyze`, {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Server error');
      }

      const data = await response.json();
      console.log("ðŸ“¤ Raw R output:", output);

      const groupedData = data.reduce((acc, d) => {
        if (d.Error || isNaN(d.Mean_edit)) return acc;
        const group = d.Group || 'Unknown';
        if (!acc[group]) acc[group] = { total: 0, count: 0, samples: [] };
        acc[group].total += d.Mean_edit;
        acc[group].count++;
        acc[group].samples.push({ file: d.File, replicate: d.Replicate, value: d.Mean_edit });
        return acc;
      }, {});

      const barData = Object.entries(groupedData).map(([group, { total, count, samples }]) => ({
        group,
        mean_edit: total / count,
        sampleCount: count,
        samples
      }));

      setResults({
        labels: barData.map(d => d.group),
        datasets: [{
          label: 'Mean Editing (%)',
          data: barData.map(d => d.mean_edit),
          backgroundColor: '#C00000',
          borderColor: '#C00000',
          borderWidth: 1
        }],
        rawData: barData
      });
    } catch (err) {
      console.error(err);
      setError(`Error: ${err.message}`);
    } finally {
      setLoading(false);
      setProcessingStatus('');
    }
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { display: false },
      title: { display: true, text: 'Editing by Group', color: '#C00000' },
      tooltip: {
        callbacks: {
          label: (context) => {
            const value = context.parsed.y.toFixed(2);
            const rawData = results.rawData[context.dataIndex];
            return [`Mean Editing: ${value}%`, `Samples: ${rawData.sampleCount}`];
          }
        }
      }
    },
    scales: {
      y: { beginAtZero: true, title: { display: true, text: 'Editing (%)' } },
      x: { title: { display: true, text: 'Group' } }
    }
  };

  return (
    <div className="container" style={{ padding: '20px' }}>
      <h2 style={{ color: '#C00000' }}>Analyze Sanger Data</h2>

      <label>Guide Sequence:</label>
      <input type="text" value={guideSeq} onChange={e => setGuideSeq(e.target.value.toUpperCase())} />

      <label>Upload .ab1 Files:</label>
      <input type="file" multiple accept=".ab1" onChange={handleFileUpload} />

      {metadata.map((meta, idx) => (
        <div key={idx}>
          <span>{meta.fileName}</span>
          <input placeholder="Group" value={meta.group} onChange={e => handleMetadataChange(idx, 'group', e.target.value)} />
          <input placeholder="Replicate" value={meta.replicate} onChange={e => handleMetadataChange(idx, 'replicate', e.target.value)} />
        </div>
      ))}

      <button onClick={processFiles} disabled={loading}>{loading ? 'Processing...' : 'Analyze'}</button>
      {processingStatus && <p>{processingStatus}</p>}
      {error && <div style={{ color: 'red' }}>{error}</div>}

      {results && (
        <>
          <Bar data={results} options={chartOptions} />
          <table>
            <thead><tr><th>Group</th><th>File</th><th>Replicate</th><th>Editing (%)</th></tr></thead>
            <tbody>
              {results.rawData.flatMap(group => group.samples.map((s, i) => (
                <tr key={`${group.group}-${s.file}-${i}`}>
                  <td>{group.group}</td>
                  <td>{s.file}</td>
                  <td>{s.replicate}</td>
                  <td>{s.value.toFixed(2)}</td>
                </tr>
              )))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default Analysis;
