import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import '../App.css';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, Title, Tooltip, Legend);

// ... [imports and ChartJS registration stay unchanged]

const Analysis = () => {
  const [files, setFiles] = useState([]);
  const [metadata, setMetadata] = useState([]);
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [processingStatus, setProcessingStatus] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files);
    const validFiles = uploadedFiles.filter(file => file.name.endsWith('.ab1') && file.size <= 10 * 1024 * 1024);

    if (validFiles.length !== uploadedFiles.length) {
      setError('Some files are invalid. Please upload .ab1 files under 10MB.');
      return;
    }

    setFiles(validFiles);
    setMetadata(uploadedFiles.map((file, idx) => ({
      fileName: file.name,
      group: '',
      replicate: '1',
      editing: null
    })));
    setError(null);
    setResults(null);
  };

  const handleMetadataChange = (index, field, value) => {
    const newMetadata = [...metadata];
    newMetadata[index][field] = value;
    setMetadata(newMetadata);
  };

  const processFiles = async () => {
    if (!metadata.length) return;

    setLoading(true);
    setError(null);
    setProcessingStatus('Uploading and analyzing files...');

    try {
      const formData = new FormData();
      files.forEach(file => formData.append('files', file));
      formData.append('guide_seq', '');
      formData.append('groups', JSON.stringify(metadata.map((m, i) => m.group.trim() || `${i + 1}`)));
      formData.append('replicates', JSON.stringify(metadata.map(m => m.replicate)));

      const response = await fetch(`${API_URL}/analyze`, {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Server error');
      }

      const data = await response.json();

      // Merge editing values back into metadata
      const updatedMetadata = metadata.map((meta, idx) => {
        const matched = data.find(d => d.File === meta.fileName);
        return {
          ...meta,
          group: meta.group || `${idx + 1}`,
          editing: matched?.Mean_edit ?? null
        };
      });

      const grouped = updatedMetadata.reduce((acc, m) => {
        const group = m.group;
        if (!acc[group]) acc[group] = { total: 0, count: 0, samples: [] };
        if (m.editing != null) {
          acc[group].total += m.editing;
          acc[group].count++;
        }
        acc[group].samples.push(m);
        return acc;
      }, {});

      const labels = Object.keys(grouped);
      const maxEdit = Math.max(...updatedMetadata.map(m => m.editing || 0));
      const chartMax = Math.min(100, Math.ceil(maxEdit + 10));

      const groupMeans = labels.map(group => grouped[group].total / grouped[group].count);
      const replicatePoints = {
        label: 'Replicates',
        data: labels.flatMap(group => grouped[group].samples.map(s => ({ x: group, y: s.editing }))),
        type: 'scatter',
        backgroundColor: '#000',
        pointStyle: 'circle',
        radius: 4,
        order: 2
      };

      const barData = {
        label: 'Mean Editing (%)',
        data: groupMeans,
        backgroundColor: '#C00000',
        borderColor: '#C00000',
        borderWidth: 1,
        order: 1
      };

      setMetadata(updatedMetadata); // update with editing %
      setResults({ labels, datasets: [barData, replicatePoints], chartMax });
    } catch (err) {
      console.error(err);
      setError(`Error: ${err.message}`);
    } finally {
      setLoading(false);
      setProcessingStatus('');
    }
  };

  const chartOptions = (maxY) => ({
    responsive: true,
    plugins: {
      legend: { display: false },
      title: { display: true, text: 'Editing by Group', color: '#C00000' },
    },
    scales: {
      y: {
        beginAtZero: true,
        max: maxY,
        title: { display: true, text: 'Editing (%)' }
      },
      x: {
        title: { display: true, text: 'Group' }
      }
    }
  });

  const exportToCSV = () => {
    const rows = [["File", "Group", "Replicate", "Editing (%)"]];
    metadata.forEach(m => {
      rows.push([m.fileName, m.group, m.replicate, m.editing?.toFixed(2) ?? '']);
    });

    const csvContent = "data:text/csv;charset=utf-8," + rows.map(r => r.join(",")).join("\n");
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.href = encodedUri;
    link.download = "sanger_results.csv";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="container p-5">
      <h1 className="title is-3 has-text-black mb-4">Sanger Analysis</h1>

      {/* File selector + analyze */}
      <div className="field is-grouped mb-5">
        <div className="control">
          <div className="file has-name is-rounded">
            <label className="file-label">
              <input
                className="file-input"
                type="file"
                accept=".ab1"
                multiple
                onChange={handleFileUpload}
              />
              <span className="file-cta" style={{ backgroundColor: '#C00000', color: 'white', borderRadius: '20px' }}>
                <span className="file-label">Select Files</span>
              </span>
              <span style={{ color: '#777', paddingLeft: '10px' }}>
                {files.length > 0 ? `${files.length} file(s)` : 'No files selected'}
              </span>
            </label>
          </div>
        </div>

        <div className="control">
          <button
            onClick={processFiles}
            className="button"
            style={{
              backgroundColor: '#C00000',
              color: 'white',
              borderRadius: '20px',
              padding: '10px 20px',
              border: 'none'
            }}
            disabled={loading}
          >
            {loading ? 'Processing...' : 'Analyze'}
          </button>
        </div>
      </div>

      {/* Unified table with results */}
      {metadata.length > 0 && (
        <div>
          <table className="table is-bordered has-text-black" style={{ backgroundColor: 'white', color: 'black' }}>
            <thead style={{ backgroundColor: '#e8e8e8' }}>
              <tr>
                <th style={{ color: 'black' }}>File</th>
                <th style={{ color: 'black' }}>Group</th>
                <th style={{ color: 'black' }}>Replicate</th>
                <th style={{ color: 'black' }}>% Editing</th>
              </tr>
            </thead>
            <tbody style={{ backgroundColor: '#ffffff', color: 'black' }}>
              {metadata.map((meta, idx) => (
                <tr key={idx}>
                  <td style={{ color: 'black' }}>{meta.fileName}</td>
                  <td>
                    <input
                      className="input"
                      style={{ backgroundColor: 'white', color: 'black' }}
                      value={meta.group}
                      onChange={e => handleMetadataChange(idx, 'group', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      className="input"
                      style={{ backgroundColor: 'white', color: 'black' }}
                      value={meta.replicate}
                      onChange={e => handleMetadataChange(idx, 'replicate', e.target.value)}
                    />
                  </td>
                  <td>{meta.editing != null ? meta.editing.toFixed(2) : ''}</td>
                </tr>
              ))}
            </tbody>
          </table>
          {results && (
            <p>
              <a onClick={exportToCSV} style={{ cursor: 'pointer', color: '#C00000', textDecoration: 'underline' }}>
                Export to CSV
              </a>
            </p>
          )}
        </div>
      )}

      {/* Chart (50%) */}
      {results && (
        <div className="columns mt-5">
          <div className="column is-half">
            <Bar data={results} options={chartOptions(results.chartMax)} />
          </div>
        </div>
      )}

      {processingStatus && <p>{processingStatus}</p>}
      {error && <div className="has-text-danger">{error}</div>}
    </div>
  );
};

export default Analysis;
