import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import '../App.css';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, Title, Tooltip, Legend);

const Analysis = () => {
  const [files, setFiles] = useState([]);
  const [metadata, setMetadata] = useState([]);
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [processingStatus, setProcessingStatus] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files);
    const validFiles = uploadedFiles.filter(file => file.name.endsWith('.ab1') && file.size <= 10 * 1024 * 1024);

    if (validFiles.length !== uploadedFiles.length) {
      setError('Some files are invalid. Please upload .ab1 files under 10MB.');
      return;
    }

    setFiles(validFiles);
    setMetadata(validFiles.map((file, idx) => ({
      fileName: file.name,
      group: '',
      replicate: '1'
    })));
    setError(null);
    setResults(null);
  };

  const handleMetadataChange = (index, field, value) => {
    const newMetadata = [...metadata];
    newMetadata[index][field] = value;
    setMetadata(newMetadata);
  };

  const validateInput = () => true;

  const processFiles = async () => {
    if (!validateInput()) return;

    setLoading(true);
    setError(null);
    setProcessingStatus('Uploading and analyzing files...');

    try {
      const formData = new FormData();
      files.forEach(file => formData.append('files', file));
      formData.append('guide_seq', '');
      formData.append('groups', JSON.stringify(metadata.map((m, i) => m.group.trim() || `${i + 1}`)));
      formData.append('replicates', JSON.stringify(metadata.map(m => m.replicate)));

      const response = await fetch(`${API_URL}/analyze`, {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Server error');
      }

      const data = await response.json();

      const groupedData = data.reduce((acc, d) => {
        if (d.Error || isNaN(d.Mean_edit)) return acc;
        const group = d.Group || 'Unknown';
        if (!acc[group]) acc[group] = { total: 0, count: 0, samples: [] };
        acc[group].total += d.Mean_edit;
        acc[group].count++;
        acc[group].samples.push({ file: d.File, replicate: d.Replicate, value: d.Mean_edit });
        return acc;
      }, {});

      const maxEdit = Math.max(...data.map(d => d.Mean_edit || 0));
      const chartMax = Math.min(100, Math.ceil(maxEdit + 10));

      const labels = Object.keys(groupedData);
      const groupMeans = labels.map(group => groupedData[group].total / groupedData[group].count);

      const replicatePoints = {
        label: 'Replicates',
        data: labels.flatMap((group) =>
          groupedData[group].samples.map(s => ({
            x: group,
            y: s.value
          }))
        ),
        type: 'scatter',
        backgroundColor: '#000',
        pointStyle: 'circle',
        radius: 4,
        showLine: false
      };

      setResults({
        labels,
        datasets: [
          {
            label: 'Mean Editing (%)',
            data: groupMeans,
            backgroundColor: '#C00000',
            borderColor: '#C00000',
            borderWidth: 1
          },
          replicatePoints
        ],
        rawData: Object.entries(groupedData).map(([group, info]) => ({
          group,
          ...info
        })),
        chartMax
      });
    } catch (err) {
      console.error(err);
      setError(`Error: ${err.message}`);
    } finally {
      setLoading(false);
      setProcessingStatus('');
    }
  };

  const chartOptions = (maxY) => ({
    responsive: true,
    plugins: {
      legend: { display: false },
      title: { display: true, text: 'Editing by Group', color: '#C00000' },
    },
    scales: {
      y: {
        beginAtZero: true,
        max: maxY,
        title: { display: true, text: 'Editing (%)' }
      },
      x: {
        title: { display: true, text: 'Group' }
      }
    }
  });

  return (
    <div className="container p-5">
      <h1 className="title is-3 has-text-black mb-4">Sanger Analysis</h1>

      {/* File select + analyze buttons */}
      <div className="field is-grouped mb-5">
        <div className="control">
          <div className="file has-name is-rounded">
            <label className="file-label">
              <input
                className="file-input"
                type="file"
                name="ab1files"
                accept=".ab1"
                multiple
                onChange={handleFileUpload}
              />
              <span className="file-cta" style={{ backgroundColor: '#C00000', color: 'white', borderRadius: '20px' }}>
                <span className="file-label">Select Files</span>
              </span>
              <span className="file-name" style={{ backgroundColor: '#C00000', color: 'white' }}>
                {files.length > 0 ? `${files.length} file(s)` : 'No files selected'}
              </span>
            </label>
          </div>
        </div>

        <div className="control">
          <button
            onClick={processFiles}
            className="button"
            style={{
              backgroundColor: '#C00000',
              color: 'white',
              borderRadius: '20px',
              padding: '10px 20px',
              border: 'none'
            }}
            disabled={loading}
          >
            {loading ? 'Processing...' : 'Analyze'}
          </button>
        </div>
      </div>

      {/* Metadata table */}
      {metadata.length > 0 && (
        <div style={{ maxWidth: '50%', backgroundColor: 'white' }}>
          <table className="table is-striped is-fullwidth has-background-white has-text-black mb-5">
            <thead>
              <tr>
                <th>File</th>
                <th>Group</th>
                <th>Replicate</th>
              </tr>
            </thead>
            <tbody>
              {metadata.map((meta, idx) => (
                <tr key={idx}>
                  <td>{meta.fileName}</td>
                  <td>
                    <input
                      className="input has-background-white has-text-black"
                      value={meta.group}
                      onChange={e => handleMetadataChange(idx, 'group', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      className="input has-background-white has-text-black"
                      value={meta.replicate}
                      onChange={e => handleMetadataChange(idx, 'replicate', e.target.value)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {processingStatus && <p>{processingStatus}</p>}
      {error && <div className="has-text-danger">{error}</div>}

      {/* Chart + Results Table */}
      {results && (
        <div className="columns mt-5">
          <div className="column" style={{ minHeight: '400px' }}>
            <Bar data={results} options={chartOptions(results.chartMax)} />
          </div>
          <div className="column">
            <div className="has-background-white">
              <table className="table is-striped is-fullwidth is-bordered has-text-black">
                <thead>
                  <tr>
                    <th>Group</th>
                    <th>File</th>
                    <th>Replicate</th>
                    <th>Editing (%)</th>
                  </tr>
                </thead>
                <tbody>
                  {results.rawData.flatMap(group =>
                    group.samples.map((s, i) => (
                      <tr key={`${group.group}-${s.file}-${i}`}>
                        <td>{group.group}</td>
                        <td>{s.file}</td>
                        <td>{s.replicate}</td>
                        <td>{s.value.toFixed(2)}</td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Analysis;
